examples.code:
$("#container").click(function (e) {
  $("*").removeClass("hilite");
  var $kids = $(e.target).children();
  var len = $kids.addClass("hilite").length;

  $("#results span:first").text(len);
  $("#results span:last").text(e.target.tagName);

  e.preventDefault();
  return false;
});

